import React, { useState, useEffect } from "react";
import { database, ref, set, get, onValue } from "../firebase";

const JsonEditor = () => {
  const [jsonData, setJsonData] = useState({});
  const dbRef = ref(database, "jsonData");

  // Fetch JSON data from Firebase
  useEffect(() => {
    get(dbRef).then((snapshot) => {
      if (snapshot.exists()) {
        setJsonData(snapshot.val());
      }
    });

    // Listen for real-time updates
    onValue(dbRef, (snapshot) => {
      if (snapshot.exists()) {
        setJsonData(snapshot.val());
      }
    });
  }, []);

  // Handle JSON edit
  const handleChange = (e) => {
    try {
      const parsedJson = JSON.parse(e.target.value);
      setJsonData(parsedJson);
    } catch (error) {
      console.error("Invalid JSON:", error);
    }
  };

  // Save JSON data to Firebase
  const saveJson = () => {
    set(dbRef, jsonData)
      .then(() => alert("JSON updated successfully!"))
      .catch((error) => console.error("Error updating JSON:", error));
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Remote JSON Editor</h2>
      <textarea
        value={JSON.stringify(jsonData, null, 2)}
        onChange={handleChange}
        rows={10}
        cols={50}
      />
      <br />
      <button onClick={saveJson}>Save</button>
    </div>
  );
};

export default JsonEditor;
